import React, { useContext } from "react";
import { Link } from "react-router-dom";
import "./Homepage.css";
import UserContext from "../auth/UserContext";

/** Homepage of site.
 *
 * Shows welcome message or login/register buttons.
 *
 * Routed at /
 *
 * Routes -> Homepage
 */

function Homepage() {
  const { currentUser } = useContext(UserContext);
  console.debug("Homepage", "currentUser=", currentUser);

  return (
<div className="Homepage" style={{ color: 'green', textAlign: 'center' }}>
  <h1 style={{ marginBottom: '1.5rem', fontWeight: 'bold' }}>Fitness Buddy</h1>
  <p style={{ fontSize: '1.25rem', marginBottom: '1.5rem' }}>All Exercises in one, convenient place.</p>
  {currentUser ? (
    <h2>Welcome Back, {currentUser.firstName || currentUser.username}!</h2>
  ) : (
    <div>
      <p>
        <Link style={{ marginRight: '1rem', color: 'white', backgroundColor: 'blue', padding: '0.5rem 1rem', borderRadius: '0.25rem', textDecoration: 'none' }} to="/login">
          Log in
        </Link>
        <Link style={{ color: 'white', backgroundColor: 'blue', padding: '0.5rem 1rem', borderRadius: '0.25rem', textDecoration: 'none' }} to="/signup">
          Sign up
        </Link>
      </p>
    </div>
  )}
</div>


  );
}

export default Homepage;
