import React, { useState } from 'react';
import { Typography, Box, Stack } from '@mui/material';
import Loader from './Loader';

const ExerciseVideos = ({ exerciseVideos, name }) => {
  const [selectedVideo, setSelectedVideo] = useState(null);

  if (!exerciseVideos.length) return <Loader />;

  const playVideo = (videoId) => {
    setSelectedVideo(videoId);
  };

  return (
    <Box sx={{ marginTop: { lg: '203px', xs: '20px' } }} p="20px">
      <Typography  sx={{ fontSize: { lg: '44px', xs: '25px' } }} fontWeight={700} color="white" mb="33px">
        Watch <span style={{ color: '#FF2625', textTransform: 'capitalize' }}>{name}</span> exercise videos
      </Typography>
      <Stack sx={{ flexDirection: { lg: 'row' }, gap: { lg: '110px', xs: '0px' } }} justifyContent="flex-start" flexWrap="wrap" alignItems="center">
        {exerciseVideos?.slice(0, 3)?.map((item, index) => (
          <div key={index} onClick={() => playVideo(item.video.videoId)} style={{ cursor: 'pointer' }}>
            <img style={{ borderTopLeftRadius: '20px' }} src={item.video.thumbnails[0].url} alt={item.video.title} />
            <Box>
              <Typography sx={{ fontSize: { lg: '28px', xs: '18px' } }} fontWeight={600} color="white">
                {item.video.title}
              </Typography>
              <Typography fontSize="14px" color="white">
                {item.video.channelName}
              </Typography>
            </Box>
          </div>
        ))}
      </Stack>
      <div style={{ marginTop: '20px' }}>
        {selectedVideo && (
          <div>
            <iframe
              title="video-player"
              width="560"
              height="315"
              src={`https://www.youtube.com/embed/${selectedVideo}`}
              frameBorder="0"
              allow="accelerometer; autoplay; clipboard-write; encrypted-media; gyroscope; picture-in-picture"
              allowFullScreen
            ></iframe>
          </div>
        )}
      </div>
    </Box>
  );
};

export default ExerciseVideos;
