import React, { useState } from 'react';
import { Typography, Box, Stack } from '@mui/material';
import Loader from './Loader';

const ExerciseVideos = ({ exerciseVideos, name }) => {
  const [selectedVideo, setSelectedVideo] = useState(null);
  const [isVideoExpanded, setIsVideoExpanded] = useState(false);

  if (!exerciseVideos.length) return <Loader />;

  const playVideo = (videoId) => {
    setSelectedVideo(videoId === selectedVideo ? null : videoId);
    setIsVideoExpanded(!isVideoExpanded);
  };

  return (
    <Box sx={{ marginTop: { lg: '203px', xs: '20px' }, p: '20px' }}>
      <Typography sx={{ fontSize: { lg: '44px', xs: '25px' }, fontWeight: 700, color: 'white', mb: '33px' }}>
        Watch <span style={{ color: '#FF2625', textTransform: 'capitalize' }}>{name}</span> exercise videos
      </Typography>
      <Stack sx={{ flexDirection: { lg: 'row' }, gap: { lg: '110px', xs: '0px' }, justifyContent: 'flex-start', flexWrap: 'wrap', alignItems: 'flex-start' }}>
        {exerciseVideos?.slice(0, 3)?.map((item, index) => (
          <div 
            key={index} 
            style={{ 
              cursor: 'pointer', 
              marginBottom: '20px', 
              display: 'flex', 
              flexDirection: 'column', 
              alignItems: 'center', 
              maxWidth: '400px', 
              width: '100%',
              transform: isVideoExpanded ? 'scale(0.8)' : 'scale(1)',
              transition: 'transform 0.3s ease',
            }}
            onClick={() => playVideo(item.video.videoId)}
          >
            <div style={{ position: 'relative', width: '100%' }}>
              <img style={{ borderTopLeftRadius: '20px', width: '100%' }} src={item.video.thumbnails[0].url} alt={item.video.title} />
              {selectedVideo === item.video.videoId && (
                <div style={{ 
                  position: 'fixed', 
                  top: '50%', 
                  left: '50%', 
                  transform: isVideoExpanded ? 'translate(-50%, -50%) scale(2)' : 'translate(-50%, -50%) scale(1)',
                  zIndex: 1000,
                  backgroundColor: 'rgba(0, 0, 0, 0.5)',
                  borderRadius: '20px',
                  padding: '20px',
                  boxShadow: '0 0 20px 5px rgba(0, 0, 0, 0.3)',
                  display: isVideoExpanded ? 'block' : 'none',
                }}>
                  <iframe
                    title="video-player"
                    width="100%"
                    height="100%"
                    src={`https://www.youtube.com/embed/${selectedVideo}`}
                    frameBorder="0"
                    allow="accelerometer; autoplay; clipboard-write; encrypted-media; gyroscope; picture-in-picture"
                    allowFullScreen
                  ></iframe>
                </div>
              )}
            </div>
            <Box>
              <Typography sx={{ fontSize: { lg: '28px', xs: '18px' }, fontWeight: 600, color: 'white', mt: '10px', maxWidth: '300px', textAlign: 'center' }}>
                {item.video.title}
              </Typography>
              <Typography fontSize="14px" color="white" mt="5px" textAlign="center">
                {item.video.channelName}
              </Typography>
            </Box>
          </div>
        ))}
      </Stack>
    </Box>
  );
};

export default ExerciseVideos;
